```javascript
const express = require('express');
const { EventEmitter } = require('events');
const app = express();
const eventBus = new EventEmitter();

app.use(express.json());

// Event bus for inter-organ communication
const connections = new Map();

// Register an organ
app.post('/register', (req, res) => {
    const { organ, endpoint } = req.body;
    connections.set(organ, endpoint);
    console.log(`Organ registered: ${organ} -> ${endpoint}`);
    res.json({ status: 'registered', organ });
});

// Send event to specific organ
app.post('/send/:target', (req, res) => {
    const { target } = req.params;
    const { event, data } = req.body;

    if (!connections.has(target)) {
        return res.status(404).json({ error: 'Organ not found' });
    }

    // Forward to target organ
    eventBus.emit(`${target}:${event}`, data);
    console.log(`Event sent: ${target}:${event}`, data);
    res.json({ status: 'sent', target, event });
