```go
package main

import (
    "fmt"
    "time"
    "math"
)

type Metronome struct {
    startTime   time.Time
    beatCount   int
    lastBeat    time.Time
    intervals   []float64
}

func NewMetronome() *Metronome {
    now := time.Now()
    return &Metronome{
        startTime: now,
        lastBeat:  now,
        intervals: make([]float64, 0),
    }
}

func (m *Metronome) Tap() {
    now := time.Now()

    if m.beatCount > 0 {
        interval := now.Sub(m.lastBeat).Seconds()
        m.intervals = append(m.intervals, interval)
    }

    m.beatCount++
    m.lastBeat = now

    fmt.Printf("Beat %d à %.3fs\n", m.beatCount, now.Sub(m.startTime).Seconds())
}

func (m *Metronome) GetTempo() float64 {
    if len(m.intervals) < 2 {
        return 0
    }

    // Moyenne des intervalles
    sum := 0.0
    for _, interval := range m.intervals {
        sum += interval
    }
    avgInterval := sum / float64(len(m.intervals))

    // BPM = 60 / intervalle_en_secondes
    return 60.0 / avgInterval
}

func (m *Metronome) GetConfidence() float64 {
    if len(m.intervals) < 3 {
        return 0
    }

    avg := 60.0 / (m.intervals[len(m.intervals)-1])

    // Variance des derniers beats
    variance := 0.0
    for i := max(0, len(m.intervals)-5); i < len(m.intervals); i++ {
        bpm := 60.0 / m.intervals[i]
        variance += math.Pow(bpm-avg, 2)
    }
    variance /= float64(min(5, len(m.intervals)))

    // Confiance inversement proportionnelle à la variance
    return math.Max(0, 1.0 - (variance / 100.0))
}

func max(a, b int) int {
    if a > b { return a }
    return b
}

func min(a, b int) int {
    if a < b { return a }
    return b
}
