                confidence := metro.GetConfidence()
                fmt.Printf("Tempo détecté: %.1f BPM (confiance: %.2f)\n", 
                    tempo, confidence)
            }

        case <-done:
            finalTempo := metro.GetTempo()
            finalConfidence := metro.GetConfidence()
            fmt.Printf("🎵 Tempo final: %.1f BPM (confiance: %.2f)\n", 
                finalTempo, finalConfidence)
            fmt.Printf("Total beats comptés: %d\n", metro.beatCount)
            return
        }
    }
}
