```cpp
#include <chrono>
#include <thread>
#include <iostream>
#include <atomic>

class Metronome {
private:
    std::atomic<bool> running{false};
    std::atomic<int> bpm{120};
    std::chrono::high_resolution_clock::time_point last_tick;

public:
    void set_bpm(int new_bpm) {
        bpm.store(new_bpm);
    }

    void start() {
        running.store(true);
        last_tick = std::chrono::high_resolution_clock::now();

        while (running.load()) {
            tick();
            wait_next_beat();
        }
    }

    void stop() {
        running.store(false);
    }

private:
    void tick() {
        auto now = std::chrono::high_resolution_clock::now();
        auto elapsed = std::chrono::duration_cast<std::chrono::microseconds>(now - last_tick);

        std::cout << "♪ " << elapsed.count() << "μs" << std::endl;
        last_tick = now;
    }

    void wait_next_beat() {
        // 60 000 000 microsecondes = 1 minute
        int interval_microsec = 60000000 / bpm.load();
        std::this_thread::sleep_for(std::chrono::microseconds(interval_microsec));
    }
};
```

**mais c'est pas assez précis.**

pour un tempo parfait il faut:

```cpp
#include <sys/timerfd.h>
#include <unistd.h>

class PrecisionMetronome {
private:
    int timer_fd;
    bool running = false;

public:
    void start_bpm(int bpm) {
        timer_fd = timerfd_create(CLOCK_MONOTONIC, 0);

        // Calcul précis: nanoseconds entre beats
        long nsec_per_beat = 60000000000L / bpm;

        struct itimerspec timer_spec;
        timer_spec.it_value.tv_sec = 0;
        timer_spec.it_value.tv_nsec = nsec_per_beat;
        timer_spec.it_interval.tv_sec = 0;
        timer_spec.it_interval.tv_nsec = nsec_per_beat;

        timerfd_settime(timer_fd, 0, &timer_spec, nullptr);

        running = true;
        uint64_t beat_count = 0;

        while (running) {
            uint64_t expirations;
            read(timer_fd, &expirations, sizeof(expirations));

            beat_count += expirations;

            // Tick audible (ou visuel)
            tick(beat_count);
        }

        close(timer_fd);
    }

private:
    void tick(uint64_t beat_num) {
        if (beat_num % 4 == 1) {
            std::cout << "♫"; // downbeat
        } else {
            std::cout << "♪"; // beat
        }
        std::cout.flush();

        if (beat_num % 16 == 0) {
            std::cout << std::endl;
        }
    }
};
