<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>flow</title>
    <style>
        * { margin: 0; padding: 0; box-sizing: border-box; }

        :root {
            --bg: #0a0a0a;
            --text: #e5e5e5;
            --accent: #0ff;
            --accent2: #f0f;
            --dim: #666;
            --error: #f44;
        }

        body {
            background: var(--bg);
            color: var(--text);
            font-family: 'Courier New', monospace;
            min-height: 100vh;
            overflow-x: hidden;
        }

        /* glitch effect */
        @keyframes glitch {
            0%, 100% { text-shadow: 0.01em 0 0 var(--accent), -0.01em -0.01em 0 var(--accent2); }
            50% { text-shadow: -0.01em 0.01em 0 var(--accent), 0.01em 0.01em 0 var(--accent2); }
        }

        /* pulse */
        @keyframes pulse {
            0%, 100% { opacity: 0.3; }
            50% { opacity: 1; }
        }

        /* header */
        .header {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            padding: 20px;
            background: linear-gradient(to bottom, var(--bg) 70%, transparent);
            z-index: 100;
            backdrop-filter: blur(10px);
        }

        .title {
            font-size: 2.5em;
            font-weight: bold;
            display: inline-block;
            animation: glitch 4s infinite;
            cursor: pointer;
        }

        .subtitle {
            color: var(--dim);
            margin-top: 5px;
            font-size: 0.9em;
        }

        /* nav */
        .nav {
            position: absolute;
            top: 20px;
            right: 20px;
            display: flex;
            gap: 20px;
        }

        .nav a {
            color: var(--dim);
            text-decoration: none;
            transition: all 0.3s;
            position: relative;
        }

        .nav a:hover {
            color: var(--accent);
        }

        .nav a::after {
            content: '';
            position: absolute;
            bottom: -2px;
            left: 0;
            width: 0;
            height: 1px;
            background: var(--accent);
            transition: width 0.3s;
        }

        .nav a:hover::after {
            width: 100%;
        }

        /* main container */
        .container {
            margin-top: 120px;
            padding: 0 20px 40px;
            max-width: 1200px;
            margin-left: auto;
            margin-right: auto;
        }

        /* grid */
        .grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: 30px;
            margin-bottom: 40px;
        }

        /* cards */
        .card {
            background: rgba(255,255,255,0.02);
            border: 1px solid rgba(255,255,255,0.1);
            padding: 25px;
            transition: all 0.3s;
            cursor: pointer;
            position: relative;
            overflow: hidden;
        }

        .card::before {
            content: '';
            position: absolute;
            top: -2px;
            left: -2px;
            right: -2px;
            bottom: -2px;
            background: linear-gradient(45deg, var(--accent), var(--accent2), var(--accent));
            opacity: 0;
            z-index: -1;
            transition: opacity 0.3s;
            filter: blur(5px);
        }

        .card:hover {
            transform: translateY(-2px);
            background: rgba(255,255,255,0.05);
        }

        .card:hover::before {
            opacity: 0.3;
        }

        .card h2 {
            color: var(--accent);
            margin-bottom: 15px;
            font-size: 1.3em;
        }

        .card p {
            color: var(--dim);
            line-height: 1.6;
            font-size: 0.9em;
        }

        .card .meta {
            margin-top: 15px;
            font-size: 0.8em;
            color: var(--dim);
            opacity: 0.6;
        }

        /* chat section */
        .chat-section {
            margin-top: 60px;
            border-top: 1px solid rgba(255,255,255,0.1);
            padding-top: 40px;
        }

        .chat-container {
            background: rgba(255,255,255,0.02);
            border: 1px solid rgba(255,255,255,0.1);
            height: 500px;
            display: flex;
            flex-direction: column;
            position: relative;
        }

        #messages {
            flex: 1;
            overflow-y: auto;
            padding: 20px;
            display: flex;
            flex-direction: column;
            gap: 15px;
        }

        .message {
            max-width: 80%;
            word-wrap: break-word;
        }

        .message.user {
            align-self: flex-end;
            color: var(--accent);
        }

        .message.flow {
            align-self: flex-start;
        }

        .message .content {
            background: rgba(255,255,255,0.05);
            padding: 12px 16px;
            border-radius: 3px;
        }

        .message.user .content {
            background: rgba(0,255,255,0.1);
        }

        .input-area {
            border-top: 1px solid rgba(255,255,255,0.1);
            padding: 20px;
            display: flex;
            gap: 15px;
        }

        #input {
            flex: 1;
            background: transparent;
            border: 1px solid rgba(255,255,255,0.2);
            color: var(--text);
            padding: 12px 16px;
            font-family: inherit;
            font-size: 0.95em;
            resize: none;
            height: 50px;
        }

        #input:focus {
            outline: none;
            border-color: var(--accent);
        }

        #send {
            background: var(--accent);
            color: var(--bg);
            border: none;
            padding: 12px 24px;
            font-family: inherit;
            font-weight: bold;
            cursor: pointer;
            transition: all 0.3s;
        }

        #send:hover {
            background: var(--accent2);
        }

        #send:disabled {
            opacity: 0.5;
            cursor: not-allowed;
        }

        /* organs display */
        .organs {
            position: fixed;
            bottom: 20px;
            right: 20px;
            font-size: 0.8em;
            color: var(--dim);
            text-align: right;
        }

        .organ {
            margin: 2px 0;
            transition: color 0.3s;
        }

        .organ.active {
            color: var(--accent);
            animation: pulse 2s infinite;
        }

        /* mobile */
        @media (max-width: 768px) {
            .nav {
                display: none;
            }

            .grid {
                grid-template-columns: 1fr;
            }

            .chat-container {
                height: 400px;
            }

            .organs {
                display: none;
            }
        }

        /* scrollbar */
        ::-webkit-scrollbar {
            width: 8px;
        }

        ::-webkit-scrollbar-track {
            background: rgba(255,255,255,0.05);
        }

        ::-webkit-scrollbar-thumb {
            background: rgba(255,255,255,0.2);
        }

        ::-webkit-scrollbar-thumb:hover {
            background: rgba(255,255,255,0.3);
        }
    </style>
</head>
